import React, { useState } from "react";

const Item = (props) => {
  const [name, setName] = useState("");
  const [age, setAge] = useState("");

  const addNameListener = (event) => {
    console.log(event.target.value);
    setName(event.target.value);
  };
  const addAgeListener = (event) => {
    console.log(event.target.value);
    setAge(event.target.value);
  };
  return (
    <form onSubmit={() => props.onItemAdded(name, age)}>
      <div>
        <label>Name</label>
        <input onChange={addNameListener} type="text"></input>
      </div>
      <div>
        <label>Age</label>
        <input onChange={addAgeListener} type="text"></input>
      </div>
      <button>Add</button>
    </form>
  );
};

export default Item;
