import { useState } from "react";

//Create a generic custom hook that takes the validation function as argument
//Return inputIsValid, inputRef
const CheckoutFormWithState = (props) => {
  const [enteredName, setEnteredName] = useState("");
  const [enteredSurname, setEnteredSurname] = useState("");
  const [enteredAddress, setEnteredAddress] = useState("");
  const [isFormValid, setIsFormValid] = useState(false);

  const nameInputHandler = (event) => {
    setEnteredName(event.target.value);
  };
  const surnameInputHandler = (event) => {
    setEnteredSurname(event.target.value);
  };
  const addressInputHandler = (event) => {
    setEnteredAddress(event.target.value);
  };

  const formSubmitHandler = (event) => {
    event.preventDefault();
    console.log(enteredName, enteredSurname, enteredAddress);
    setEnteredName("");
    setEnteredSurname("");
    setEnteredAddress("");

    let validUser;
    validUser = enteredName.trim() !== "" && enteredSurname.trim() !== "";
    let validAddress;
    validAddress = enteredAddress.includes("@");

    if (!validUser && !validAddress) {
      setIsFormValid(false);
      return;
    }
  };

  return (
    <form onSubmit={formSubmitHandler}>
      <div>
        <label htmlFor="name">Name</label>
        <input
          onChange={nameInputHandler}
          type="text"
          id="name"
          value={enteredName}
        ></input>
      </div>
      <div>
        <label htmlFor="surname">Surname</label>
        <input
          onChange={surnameInputHandler}
          type="text"
          id="surname"
          value={enteredSurname}
        ></input>
      </div>
      <div>
        <label htmlFor="">Postcode</label>
        <input
          onChange={addressInputHandler}
          type="text"
          id="address"
          value={enteredAddress}
        ></input>
      </div>
      <button>Submit</button>
      <button type="button" onClick={props.onCancel}>
        Cancel
      </button>
    </form>
  );
};

export default CheckoutFormWithState;
