import { useState, useRef } from "react";

const useInput = (inputValue, inputValidation) => {
  const [inputIsValid, setInputIsValid] = useState(true);
  const inputValueIsValid = inputValidation(inputValue);

  setInputIsValid(inputValueIsValid);

  return {
    inputIsValid,
    inputValueIsValid,
  };
};

export default useInput;
