import { useState } from "react";

import Modal from "../UI/Modal";

const Cart = (props) => {
  const [cartVisible, setCartVisible] = useState(true);

  const hideCartHandler = () => {
    setCartVisible(false);
  };
  if (cartVisible) {
    return (
      <Modal>
        <h3>Sushi</h3>
        <h4>12.99</h4>
        <button>Close</button>
      </Modal>
    );
  } else {
    return <div></div>;
  }
};
export default Cart;
