import { useState, useRef } from "react";

const useInput = (inputValidation) => {
  const inputRef = useRef();
  const [inputIsValid, setInputIsValid] = useState(true);
  const inputValueIsValid = inputValidation();

  return {
    inputRef,
    inputIsValid,
    inputValueIsValid,
  };
};

export default useInput;
